// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

enum OrderStatus {
    None,
    Placed,
    Completed,
    Refunded,
    Cancelled,
    Recorded // off-chain record
}

struct Product {
    address seller;
    string  name;          // e.g., "Rice (BR-28)"
    string  unit;          // e.g., "kg", "sack"
    uint256 unitPriceWei;  // price per unit
    uint256 availableQty;  // remaining units
    bool    active;
}

struct Order {
    uint256 id;
    uint256 productId;
    address buyer;
    address seller;
    uint256 quantity;
    uint256 unitPriceWei;
    uint256 totalWei;
    OrderStatus status;
    uint64  placedAt;
}

contracts/common/ReentrancyGuard.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Reentrancy} from "./Errors.sol";

abstract contract ReentrancyGuard {
    uint256 private _status;

    modifier nonReentrant() {
        if (_status == 1) revert Reentrancy();
        _status = 1;
        _;
        _status = 0;
    }
}

contracts/common/Ownable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {OnlyOwner, ZeroAddress} from "./Errors.sol";
import {Events} from "./Events.sol";

abstract contract Ownable is Events {
    address public owner;

    modifier onlyOwner() {
        if (msg.sender != owner) revert OnlyOwner();
        _;
    }

    constructor() {
        owner = msg.sender;
        emit OwnershipTransferred(address(0), msg.sender);
    }

    function transferOwnership(address newOwner) public onlyOwner {
        if (newOwner == address(0)) revert ZeroAddress();
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }
}

